def createToken(
    name,
    role,
    group,
):
####+END:
    token = {}
    userInfo={}
    userInfo['userName'] = str(name)
    userInfo['role'] = role.split(',')
    userInfo['resGroupIds'] = group.split(',')
    token['userInfo'] = userInfo
    return token



    
